define({"topics" : [{"title":"Text Areas","shortdesc":"\n               <p class=\"shortdesc\">How to draw text areas in <a href=\"glossary\/g_afr.html\" title=\"A software program that scans image files and outputs text using OCR technology.\"><dfn class=\"term\">.</dfn></a></p>\n            ","href":"source\/t_text_areas.html","attributes": {"data-id":"t.text-areas",},"menu": {"hasChildren":false,},"tocID":"t.text-areas-d66e1156","topics":[]},{"title":"Order Areas","shortdesc":"\n               <p class=\"shortdesc\">How to order areas correctly for text output.</p>\n            ","href":"source\/t_order_areas.html","attributes": {"data-id":"t.order-footnote-areas",},"menu": {"hasChildren":false,},"tocID":"t.order-footnote-areas-d66e1178","topics":[]},{"title":"Modify Areas","shortdesc":"\n               <p class=\"shortdesc\">Areas must be drawn in a basic box shape, but they can be altered to subtract or add         to part of the box. </p>\n            ","href":"source\/t_modify_areas.html","attributes": {"data-id":"t.modify-areas",},"menu": {"hasChildren":false,},"tocID":"t.modify-areas-d66e1198","topics":[]},{"title":"Footnote Areas","shortdesc":"\n               <p class=\"shortdesc\">Footnotes always output as the last areas on the page.</p>\n            ","href":"source\/c_footnote_areas.html","attributes": {"data-id":"c.footnote-areas",},"menu": {"hasChildren":false,},"tocID":"c.footnote-areas-d66e1218","topics":[]},{"title":"Table Areas","shortdesc":"\n               <p class=\"shortdesc\">How to draw table areas in <a href=\"glossary\/g_afr.html\" title=\"A software program that scans image files and outputs text using OCR technology.\"><dfn class=\"term\">ABBYY FineReader</dfn></a></p>\n            ","href":"source\/t_table_areas.html","attributes": {"data-id":"t.table-areas",},"menu": {"hasChildren":false,},"tocID":"t.table-areas-d66e1238","next":"t.table-areas-d66e1238",},{"title":"Illustrations","shortdesc":"\n               <p class=\"shortdesc\">When drawing boxes, leave out illustrations and their captions.</p>\n            ","href":"source\/c_illustrations.html","attributes": {"data-id":"c.illustrations",},"menu": {"hasChildren":false,},"tocID":"c.illustrations-d66e1279","topics":[]},{"title":"Catchwords","shortdesc":"\n               <p class=\"shortdesc\">eb03 puts an extra word at the bottom of the page that should not be included in text         boxes.</p>\n            ","href":"source\/c_catchwords.html","attributes": {"data-id":"c.catchwords",},"menu": {"hasChildren":false,},"tocID":"c.catchwords-d66e1299","topics":[]},{"title":"Author Initials","shortdesc":"\n               <p class=\"shortdesc\">Who wrote it?</p>\n            ","href":"source\/c_author_initials.html","attributes": {"data-id":"c.author-initials",},"menu": {"hasChildren":false,},"tocID":"c.author-initials-d66e1319","topics":[]},{"title":"Pages without Text","shortdesc":"\n               <p class=\"shortdesc\">Numbered pages with only illustrations or drawings require minimal         treatment.</p>\n            ","href":"source\/t_pages_withou-text.html","attributes": {"data-id":"t.unnumbered-pages",},"menu": {"hasChildren":false,},"tocID":"t.unnumbered-pages-d66e1339","topics":[]},{"title":"Headers","shortdesc":"\n               <p class=\"shortdesc\">Include two-column titles as an entry term.</p>\n            ","href":"source\/c_headers.html","attributes": {"data-id":"c_headers",},"menu": {"hasChildren":false,},"tocID":"c_headers-d66e1359","topics":[]}]});